package baitap;

public class Point_01 {
	public double x;
	public double y;
	public Point_01() {
		// TODO Auto-generated constructor stub
	}
	@Override
	public String toString() {
		return "Point_01 [x=" + x + ", y=" + y + "]";
	}
	public Point_01(double x, double y) {
		super();
		this.x = x;
		this.y = y;
	}
	public double getX() {
		return x;
	}
	public void setX(double x) {
		this.x = x;
	}
	public double getY() {
		return y;
	}
	public void setY(double y) {
		this.y = y;
	}	
	

}
